import React from 'react';

const InvoiceSection = () => {
  const editProduct = () => {
    // Your editProduct logic here
  };

  const deleteProduct = () => {
    // Your deleteProduct logic here
  };
  return (
    <section id="invoice-section">
      {/* Existing content or any React components you want to include */}
      <h2>Pharmacy Invoice</h2>
      <div id="customer-info">
        <h3>Customer Information</h3>
        <p><strong>Name:</strong> John Doe</p>
        <p><strong>Email:</strong> john@example.com</p>
        <p><strong>Address:</strong> 123 Main Street, Cityville</p>
      </div>

      <div id="product-details">
        <h3>Product Details</h3>
        <table>
          <thead>
            <tr>
              <th>Product</th>
              <th>Quantity</th>
              <th>Price per Unit</th>
              <th>Total</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>Medicine A</td>
              <td>2</td>
              <td>$10.00</td>
              <td>$20.00</td>
              <td>
                <button onClick={editProduct}>Edit</button>
                <button onClick={deleteProduct}>Delete</button>
              </td>
            </tr>
            {/* Add more rows for additional products */}
          </tbody>
        </table>
      </div>

      <div id="total-amount">
        <p><strong>Total Amount:</strong> $20.00</p>
      </div>
    </section>
  );
}

export default InvoiceSection;
