import React, { Fragment } from 'react';
import { useState } from 'react';

const CrudSection = () => {
  const [productName, setProductName] = useState('');
  const [quantity, setQuantity] = useState('');
  const [price, setPrice] = useState('');

  const [CustomerName, setCustomerName] = useState('');
  const [MobileNo, setMobileNo] = useState('');
  const [Email, setEmail] = useState('');
  const [Address, setAddress] = useState('');
  const [BillingType, setBillingType] = useState('');
  

  const addProduct = () => {
    // Your addProduct logic here
    const apiUrl = 'http://localhost:8080/addProduct';

    fetch(apiUrl, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        productName,
        quantity,
        price,
      }),
    })
      .then(response => response.json())
      .then(data => {
        console.log('Product added successfully:', data);
        // Add any additional logic or state updates as needed
      })
      .catch(error => {
        console.error('Error adding product:', error);
        // Handle errors appropriately
      });

  };

  const addCustomer = () => {
    // Your addProduct logic here
    // Use productName, quantity, and price state values
  };

  return (
    <section id="crud-section">
      <div className="crud-container">
        <div className="crud-section">
          <h2>Manage Products</h2>
          <form id="product-form">
            {/* Product form fields */}
            <form id="product-form">
        <label htmlFor="productName">Product Name:</label>
        <input
          type="text"
          id="productName"
          name="productName"
          value={productName}
          onChange={(e) => setProductName(e.target.value)}
          required
        />

        <label htmlFor="quantity">Quantity:</label>
        <input
          type="number"
          id="quantity"
          name="quantity"
          value={quantity}
          onChange={(e) => setQuantity(e.target.value)}
          required
        />

        <label htmlFor="price">Price per Unit:</label>
        <input
          type="number"
          id="price"
          name="price"
          step="0.01"
          value={price}
          onChange={(e) => setPrice(e.target.value)}
          required
        />

        <button type="button" onClick={addProduct}>
          Add Product &nbsp;
        </button>
      </form>
      &nbsp;
      &nbsp;
      <section></section> 
     </form>
  </div>

        <div className="crud-section">
          <h2>Manage Customers</h2>
          <form id="customer-form">
          {/* Customer form fields */}
         <form id="product-form">
        <label htmlFor="CustomerForm">Customer Name:</label>
        <input
          type="text"
          id="CustomerName"
          name="CustomerName"
          value={CustomerName}
          onChange={(e) => setCustomerName(e.target.value)}
          required
        />

        <label htmlFor="Mobile">Mobile NO:</label>
        <input
          type="number"
          id="quantity"
          name="quantity"
          value={quantity}
          onChange={(e) => setQuantity(e.target.value)}
          required
        />

        <label htmlFor="Email">Email</label>
        <input
          type="Email"
          id="Email"
          name="Email"
          step="0.01"
          value={price}
          onChange={(e) => setPrice(e.target.value)}
          required
        />

      <label htmlFor="Address">Address </label>
        <input
          type="textBox"
          id="Address"
          name="Address"
          value={Address}
          onChange={(e) => setAddress(e.target.value)}
          required
        />

      <label htmlFor="BillingType">BillingType</label>
        <input
          type="Choice"
          id="BillingType"
          name="BillingType"
          value={BillingType}
          onChange={(e) => setAddress(e.target.value)}
          required
        />

        <button type="button" onClick={addProduct}>
          Add Product &nbsp;
        </button>
      </form>
      &nbsp;
      &nbsp;
      <section></section> 
    </form>
        </div>
      </div>
    </section>

  );
}

export default CrudSection;
